
module.exports.reference = () => {
  var str = require('../randomstring'); //if there is an error in finding 'randomstring', check its location/path: ex: ('../randomstring')
  str= str.generate(4);
  var now = new Date();
  var date= now.getFullYear() +"" +now.getMonth() +""+ now.getDate();
  date = date.toString();
  date = date.substring(2, date.length);
  var hour = now.getHours()+""+now.getMinutes()+""+now.getSeconds()+""+now.getMilliseconds();
  hour= hour.toString();
  var ref1 = date.concat(str);
  var ref = ref1.concat(hour);
return ref;

}
